  const msg = ref("something")
  //actions
  const addCount = () =&gt; count.value++
  const subCount = () =&gt; count.value--
  //getters
  const double = computed(() =&gt; count.value * 2)
  return {
    count,
    msg,
    addCount,
    subCount,
    double
  }
},
